//背景窗口回调函数，WM的裁剪机制确保了仅重绘无效区域，并不是完全的执行下面的语句！！！！
static void _cbBkWindow(WM_MESSAGE *pMsg)
{
    //根据不同的消息类型处理
    switch (pMsg->MsgId)
    {
    //WM_PAINT消息，窗口重绘自身
    case WM_PAINT:
        //为当前窗口填充背景
        GUI_SetBkColor(GUI_BLACK);
        GUI_Clear();
        break;
    default:
        //此函数可处理未处理的消息
        WM_DefaultProc(pMsg);
        break;
    }
}

//窗口回调函数
static void _cbWindow(WM_MESSAGE *pMsg)
{
    GUI_RECT Rect;
    if (wifi_flag == 1)
    {
        GUI_DrawBitmap(&bmwifi, 3, 1);
    }
    else
    {
        GUI_DrawBitmap(&bmnowifi, 3, 1);
    }
    switch (pMsg->MsgId)
    {
    case WM_PAINT:
        WM_GetInsideRect(&Rect);                //返回活动窗口减去边界后的尺寸,即客户区坐标
        GUI_SetBkColor(GUI_WHITE);              //背景色
        GUI_SetColor(GUI_LIGHTBLUE);
        GUI_ClearRectEx(&Rect);                 //清除Rect区域,既绘制背景
        GUI_DrawRectEx(&Rect);                  //绘制Rect区域,既绘制框框
        GUI_SetTextMode(GUI_TEXTMODE_TRANS);    //透明文本模式
        GUI_SetColor(GUI_BLUE);                 //字体颜色
        GUI_SetFont(&GUI_Font24_ASCII);         //设置字体
        GUI_DispDecAt(g_year, 60, 2, 4);          //显示年
        GUI_DispCharAt(47, 108, 2);             //显示 /
        GUI_DispDecAt(g_month, 116, 2, 2);        //显示月
        GUI_DispCharAt(47, 140, 2);             //显示 /
        GUI_DispDecAt(g_day, 148, 2, 2);          //显示日
        GUI_DispCharAt(32, 172, 2);             //显示空格
        GUI_DispDecAt(g_hour, 180, 2, 2);         //显示时
        GUI_DispCharAt(58, 204, 2);             //显示冒号
        GUI_DispDecAt(g_minute, 212, 2, 2);       //显示分
        break;
    default:
        WM_DefaultProc(pMsg);
        break;
    }
}

void CreateWindowTime(void)
{
    Time_Init();
    WM_SetCallback(WM_HBKWIN, _cbBkWindow); //设置背景桌面的回调函数
    //创建前景窗口，尺寸150*100
    g_time_win = WM_CreateWindow(0, 0, 240, 27, WM_CF_SHOW | WM_CF_MEMDEV, _cbWindow, 0); //WM_CF_SHOW创建后显示
}

